import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import Header from './Header';
import Footer from './Footer';

interface CartItem {
  id: number;
  image: string;
  title: string;
  price: number;
  quantity: number;
}

const Container = styled.section`
  background-color: #fff;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const MainContent = styled.div`
  background-color: #eef2f4;
  display: flex;
  width: 100%;
  flex-direction: column;
  align-items: center;
  justify-content: stretch;
  padding: 21px 0 179px;
`;

const Divider = styled.div<{ top?: boolean }>`
  align-self: stretch;
  min-height: 0px;
  margin-top: ${props => props.top ? '100px' : '10px'};
  width: 99.9%;
  border: 1px solid #d5d5d5;
`;

const BreadcrumbSort = styled.div`
  display: flex;
  width: 100%;
  max-width: 70%;
  gap: 2px;
  flex-wrap: wrap;
  justify-content: space-between;
  margin: 9px 0 0 0px;

  @media (max-width: 991px) {
    width: 70%;
    justify-content: center;
    gap: 2rem;
  }
`;

const Breadcrumb = styled.div`
  align-self: start;
  display: flex;
  gap: 12px;
  color: #000;
  font: 15px Open Sans, sans-serif;
`;

const BreadcrumbItem = styled.span<{ bold?: boolean }>`
  align-self: stretch;
  border-radius: 10px;
  background-color: #fff;
  padding: 4px 14px;
  font: ${props => props.bold ? '700' : '400'} 15px Open Sans, sans-serif;
`;

const CartContainer = styled.div`
  align-self: center;
  display: flex;
  margin-top: 38px;
  width: 100%;
  max-width: 70%;
  flex-direction: column;
  font-family: Open Sans, sans-serif;
  color: #000;
  font-weight: 700;

  @media (max-width: 991px) {
    max-width: 100%;
    padding: 0 20px;
  }
`;

const CartHeader = styled.h1`
  font-size: 25px;
  align-self: center;
`;

const ContinueShopping = styled(Link)`
  font-size: 15px;
  font-weight: 500;
  align-self: center;
  cursor: pointer;
  text-decoration: none;
  color: inherit;
`;

const CartSummary = styled.div`
  display: flex;
  width: 100%;
  max-width: 75%;
  gap: 25px;
  font-size: 15px;
  font-weight: 500;
  white-space: nowrap;
  flex-wrap: wrap;
  justify-content: space-between;
  margin: 41px 0 0 50px;

  @media (max-width: 991px) {
    display: none;
  }
`;

const CartSummaryColumn = styled.div`
  display: flex;
  gap: 40px 120px;

  @media (max-width: 991px) {
    white-space: initial;
  }
`;

const CartItem = styled.div`
  border-radius: 10px;
  background-color: #fff;
  display: flex;
  margin-top: 18px;
  width: 100%;
  align-items: center;
  gap: 20px;
  font-size: 15px;
  justify-content: space-between;
  padding: 15px 0px;

  @media (max-width: 991px) {
    align-self: center;
  }
`;

const CartItemContent = styled.div`
  align-self: stretch;
  display: flex;
  flex-wrap: wrap;
  gap: 25px;
  padding-left: 30px;

  @media (max-width: 1200px) {
    flex-direction: column;
    padding-left: 10px;
  }
`;

const ProductImage = styled.img`
  aspect-ratio: 1.06;
  object-fit: contain;
  object-position: center;
  width: 89px;
  border-radius: 10px;
`;

const ProductDescription = styled.div`
  flex-basis: auto;
  margin: auto 0;
`;

const ProductDetails = styled.div`
  align-self: stretch;
  display: flex;
  align-items: center;
  gap: 40px 74px;
  white-space: nowrap;
  margin: auto 0;

  @media (max-width: 991px) {
    white-space: initial;
    gap: 20px 5px;
  }
`;

const ProductPrice = styled.div`
  align-self: stretch;
  margin: auto 0;
`;

const QuantitySelector = styled.div`
  border-radius: 10px;
  align-self: stretch;
  display: flex;
  text-align: center;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
  padding: 10px 20px;
  border: 1px solid rgba(228, 228, 228, 1);
`;

const QuantityButton = styled.button`
  padding: 5px 10px;
  font-size: 18px;
  border: none;
  color: #000;
  align-items: center;
  cursor: pointer;
  border-radius: 5px;
  transition: 0.3s;

  &:hover {
    background-color: black;
    color: #fff;
  }
`;

const RemoveIcon = styled.img`
  aspect-ratio: 1;
  object-fit: contain;
  object-position: center;
  width: 19px;
  align-self: stretch;
  // margin: auto 0;
  margin-right: 10%;
  cursor: pointer;
`;

const SubTotal = styled.div`
  align-self: end;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  width: 100%;
  max-width: 551px;

  @media (max-width: 991px) {
    align-items: center;
    padding: 0 20px;
  }
`;

const SubtotalSection = styled.div`
  width: 100%;
  max-width: 551px;
  height: 1px;
  border: 1px solid #d5d5d5;
  margin-top: 36px;
`;

const SubtotalContainer = styled.div`
  display: flex;
  width: 100%;
  max-width: 324px;
  gap: 20px;
  justify-content: space-between;
  margin: 23px 0;
  padding-right: 20px;

  @media (max-width: 991px) {
    padding-right: 0;
  }
`;

const SubtotalLabel = styled.div`
  font-size: 15px;
`;

const SubtotalAmount = styled.div`
  font-size: 25px;
`;

const CheckoutDivider = styled.div`
  width: 100%;
  max-width: 551px;
  height: 1px;
  border: 1px solid #d5d5d5;
`;

const TaxShippingInfo = styled.div`
  font-size: 15px;
  font-weight: 500;
  margin: 10px 0;
  text-align: right;
  width: 100%;
  max-width: 424px;

  @media (max-width: 991px) {
    text-align: center;
  }
`;

const CheckoutButton = styled(Link)`
  border-radius: 30px;
  background-color: rgba(0, 85, 182, 1);
  margin-top: 32px;
  text-decoration: none;
  text-align: center;
  font-size: 15px;
  color: #fff;
  padding: 19px 150px;
  cursor: pointer;
  width: fit-content;

  @media (max-width: 991px) {
    padding: 15px 50px;
    align-self: center;
  }
`;

const Cart: React.FC = () => {
  const [cartItems, setCartItems] = useState<CartItem[]>([
    {
      id: 1,
      image: '/imgs/Rectangle 62.png',
      title: 'Lorem ipsum dolor',
      price: 50.00,
      quantity: 1
    },
    {
      id: 2,
      image: '/imgs/Rectangle 62 (4).png',
      title: 'Lorem ipsum dolor',
      price: 99.99,
      quantity: 1
    },
    {
      id: 3,
      image: '/imgs/asi.png',
      title: 'Lorem ipsum dolor',
      price: 100.00,
      quantity: 2
    }
  ]);

  const updateQuantity = (id: number, change: number) => {
    setCartItems(items =>
      items.map(item =>
        item.id === id
          ? { ...item, quantity: Math.max(1, item.quantity + change) }
          : item
      )
    );
  };

  const removeItem = (id: number) => {
    setCartItems(items => items.filter(item => item.id !== id));
  };

  const calculateSubtotal = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <Container>
      <MainContent>
        <Header />
        <Divider top />
        <BreadcrumbSort>
          <Breadcrumb>
            <BreadcrumbItem bold>yannstechub</BreadcrumbItem>
            <BreadcrumbItem>/ Cart</BreadcrumbItem>
          </Breadcrumb>
        </BreadcrumbSort>
        <Divider />
        <CartContainer>
          <CartHeader>Your Cart</CartHeader>
          <ContinueShopping to="/">Continue Shopping</ContinueShopping>
          <CartSummary>
            <div>Product</div>
            <CartSummaryColumn>
              <div>Price</div>
              <div>Quantity</div>
              <div>Total</div>
            </CartSummaryColumn>
          </CartSummary>
          {cartItems.map(item => (
            <CartItem key={item.id}>
              <CartItemContent>
                <ProductImage src={item.image} alt={item.title} />
                <ProductDescription>{item.title}</ProductDescription>
              </CartItemContent>
              <ProductDetails>
                <ProductPrice>${item.price.toFixed(2)}</ProductPrice>
                <QuantitySelector>
                  <QuantityButton onClick={() => updateQuantity(item.id, -1)}>-</QuantityButton>
                  <span>{item.quantity}</span>
                  <QuantityButton onClick={() => updateQuantity(item.id, 1)}>+</QuantityButton>
                </QuantitySelector>
                <ProductPrice>${(item.price * item.quantity).toFixed(2)}</ProductPrice>
              </ProductDetails>
              <RemoveIcon
                src="/imgs/close.png"
                alt="Remove item"
                onClick={() => removeItem(item.id)}
              />
            </CartItem>
          ))}
          <SubTotal>
            <SubtotalSection />
            <SubtotalContainer>
              <SubtotalLabel>Subtotal</SubtotalLabel>
              <SubtotalAmount>${calculateSubtotal().toFixed(2)}</SubtotalAmount>
            </SubtotalContainer>
            <CheckoutDivider />
            <TaxShippingInfo>
              Tax included and shipping calculated at checkout
            </TaxShippingInfo>
            <CheckoutButton to="/shipping-address">
              Proceed to checkout
            </CheckoutButton>
          </SubTotal>
        </CartContainer>
      </MainContent>
      <Footer />
    </Container>
  );
};

export default Cart;
