import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';

const CareersContainer = styled.div`
  display: flex;
  padding-top: 21px;
  flex-direction: column;
  align-items: center;
  min-height: 100vh;
`;

const HeaderSection = styled.header`
  position: fixed;
  border-radius: 20px;
  background-color: #f2f2f2;
  display: flex;
  width: 70%;
  overflow: hidden;
  gap: 20px;
  justify-content: space-between;
  padding: 21px 68px;
  box-shadow: 1px 5px 9px 3px rgba(0, 0, 0, 0.147);
  z-index: 1000;

  @media (max-width: 991px) {
    padding-left: 20px;
    padding-right: 20px;
  }
`;

const Logo = styled.img`
  aspect-ratio: 4.13;
  object-fit: contain;
  object-position: center;
  width: 132px;
  max-width: 100%;
`;

const NavButtons = styled.nav`
  display: flex;
  align-items: center;
  gap: 30px;
  color: #000;
  justify-content: start;
  margin: auto 0;
  font: 500 14px Open Sans, sans-serif;
`;

const NavLink = styled(Link)`
  align-self: stretch;
  margin: auto 0;
  cursor: pointer;
  text-decoration: none;
  color: inherit;
`;

const UserActions = styled.div`
  align-self: start;
  display: flex;
  margin-top: 4px;
  align-items: start;
  gap: 30px;
`;

const ActionIcon = styled.img`
  aspect-ratio: 1;
  object-fit: contain;
  object-position: center;
  width: 24px;
`;

const MainContent = styled.main`
  width: 100%;
  max-width: 80%;
  margin-top: 100px;

  @media (max-width: 991px) {
    padding-left: 20px;
    padding-right: 20px;
    max-width: 95%;
  }
`;

const PageTitle = styled.h1`
  color: #000;
  text-align: center;
  font-size: 36px;
  font-family: Inter, -apple-system, Roboto, Helvetica, sans-serif;
  font-weight: 800;
  margin-top: 46px;
`;

const JobCard = styled.article`
  border-radius: 20px;
  background-color: #fff;
  padding: 20px;
  margin: 20px 0;
  box-shadow: 1px 5px 9px 3px rgba(0, 0, 0, 0.1);
`;

const Careers: React.FC = () => {
  return (
    <CareersContainer>
      <HeaderSection>
        <Link to="/">
          <Logo src="/imgs/Logo.png" alt="YannsTechHub Logo" />
        </Link>
        <NavButtons>
          <NavLink to="/daily-deals">Daily deals</NavLink>
          <NavLink to="/shop">Shop</NavLink>
          <NavLink to="/bundle-deals">Bundle Deals</NavLink>
          <NavLink to="/support">Support</NavLink>
        </NavButtons>
        <UserActions>
          <ActionIcon src="/imgs/Search - 7.png" alt="Search" />
          <Link to="/login">
            <ActionIcon src="/imgs/Profile - 3.png" alt="User Account" />
          </Link>
          <Link to="/cart">
            <ActionIcon src="/imgs/Buy - 6 (1).png" alt="Shopping Cart" />
          </Link>
        </UserActions>
      </HeaderSection>
      <MainContent>
        <PageTitle>Career opportunities</PageTitle>
        <JobCard>
          <h3>Sales Executive</h3>
          <p>Regular Position</p>
        </JobCard>
      </MainContent>
      <Footer />
    </CareersContainer>
  );
};

export default Careers;
