import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const CartSummary = styled.div`
  display: flex;
  width: 100%;
  max-width: 75%;
  gap: 25px;
  font-size: 15px;
  font-weight: 500;
  white-space: nowrap;
  flex-wrap: wrap;
  justify-content: space-between;
  margin: 1px 0 0 50px;

  @media (max-width: 991px) {
    margin-top: 40px;
    white-space: initial;
    align-self: center;
    max-width: 50%;
    margin: 41px 0 0 0px;
  }
`;

const CartSummaryColumn = styled.div`
  display: flex;
  gap: 40px 120px;

  @media (max-width: 991px) {
    white-space: initial;
  }
`;

const CartItem = styled.div`
  border-radius: 10px;
  background-color: #fff;
  display: flex;
  margin-top: 18px;
  width: 100%;
  align-items: center;
  gap: 20px;
  font-size: 15px;
  flex-wrap: wrap;
  justify-content: space-between;
  padding: 15px 0px;

  @media (max-width: 991px) {
    max-width: 70%;
    align-self: center;
  }
`;

const CartItemContent = styled.div`
  align-self: stretch;
  display: flex;
  flex-wrap: wrap;
  gap: 25px;
  padding-left: 30px;

  @media (max-width: 1200px) {
    flex-direction: column;
  }
`;

const ProductImage = styled.img`
  aspect-ratio: 1.06;
  object-fit: contain;
  object-position: center;
  width: 89px;
  border-radius: 10px;
`;

const ProductDescription = styled.div`
  flex-basis: auto;
  margin: auto 0;
`;

const ProductDetails = styled.div`
  align-self: stretch;
  display: flex;
  align-items: center;
  gap: 40px 74px;
  white-space: nowrap;
  margin: auto 0;

  @media (max-width: 991px) {
    white-space: initial;
  }
`;

const ProductPrice = styled.div`
  align-self: stretch;
  margin: auto 0;
`;

const QuantitySelector = styled.div`
  border-radius: 10px;
  align-self: stretch;
  display: flex;
  text-align: center;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
  padding: 10px 20px;
  border: 1px solid rgba(228, 228, 228, 1);
`;

const QuantityButton = styled.button`
  padding: 5px 10px;
  font-size: 18px;
  border: none;
  color: #000;
  align-items: center;
  cursor: pointer;
  border-radius: 5px;
  transition: 0.3s;

  &:hover {
    background-color: black;
    color: #fff;
  }
`;

const RemoveIcon = styled.img`
  aspect-ratio: 1;
  object-fit: contain;
  object-position: center;
  width: 19px;
  align-self: stretch;
  margin: auto 0;
  padding-right: 30px;
  cursor: pointer;
`;

const SubTotal = styled.div`
  align-self: end;

  @media (max-width: 991px) {
    align-self: center;
  }
`;

const SubtotalSection = styled.div`
  align-self: end;
  margin-top: 36px;
  width: 551px;
  max-width: 100%;
  height: 1px;
  border: 1px solid #d5d5d5;
`;

const SubtotalContainer = styled.div`
  align-self: end;
  display: flex;
  width: 324px;
  max-width: 100%;
  gap: 20px;
  justify-content: space-between;
  margin: 23px 152px 0 0;

  @media (max-width: 991px) {
    margin-right: 10px;
  }
`;

const SubtotalLabel = styled.div`
  font-size: 15px;
  align-self: start;
  margin-top: 11px;
`;

const SubtotalAmount = styled.div`
  font-size: 25px;
`;

const CheckoutDivider = styled.div`
  align-self: end;
  margin-top: 32px;
  width: 551px;
  max-width: 100%;
  height: 1px;
  border: 1px solid #d5d5d5;
`;

const TaxShippingInfo = styled.div`
  font-size: 15px;
  font-weight: 500;
  align-self: end;
  margin: 10px 127px 0 0;

  @media (max-width: 991px) {
    margin-right: 10px;
  }
`;

const CheckoutButton = styled(Link)`
  align-self: end;
  border-radius: 30px;
  background-color: #0055b6;
  margin-top: 32px;
  min-height: 7px;
  gap: 10px;
  text-align: center;
  font-size: 15px;
  color: #fff;
  padding: 19px 150px;
  text-decoration: none;
  display: inline-block;

  @media (max-width: 991px) {
    margin-right: 10px;
    padding: 15px 20px;
  }
`;

interface CartItemType {
  id: number;
  image: string;
  description: string;
  price: number;
  quantity: number;
}

const MyChart: React.FC = () => {
  const [cartItems, setCartItems] = useState<CartItemType[]>([
    {
      id: 1,
      image: '/imgs/Rectangle 62.png',
      description: 'Lorem ipsum dolor',
      price: 50.00,
      quantity: 1
    },
    {
      id: 2,
      image: '/imgs/Rectangle 62 (4).png',
      description: 'Lorem ipsum dolor',
      price: 99.99,
      quantity: 1
    },
    {
      id: 3,
      image: '/imgs/asi.png',
      description: 'Lorem ipsum dolor',
      price: 100.00,
      quantity: 2
    }
  ]);

  const updateQuantity = (id: number, change: number) => {
    setCartItems(items =>
      items.map(item =>
        item.id === id
          ? { ...item, quantity: Math.max(1, item.quantity + change) }
          : item
      )
    );
  };

  const removeItem = (id: number) => {
    setCartItems(items => items.filter(item => item.id !== id));
  };

  const subtotal = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <>
      <CartSummary>
        <div>Product</div>
        <CartSummaryColumn>
          <div>Price</div>
          <div>Quantity</div>
          <div>Total</div>
        </CartSummaryColumn>
      </CartSummary>

      {cartItems.map(item => (
        <CartItem key={item.id}>
          <CartItemContent>
            <ProductImage src={item.image} alt={item.description} loading="lazy" />
            <ProductDescription>{item.description}</ProductDescription>
          </CartItemContent>
          <ProductDetails>
            <ProductPrice>${item.price.toFixed(2)}</ProductPrice>
            <QuantitySelector>
              <QuantityButton onClick={() => updateQuantity(item.id, -1)}>-</QuantityButton>
              <span>{item.quantity}</span>
              <QuantityButton onClick={() => updateQuantity(item.id, 1)}>+</QuantityButton>
            </QuantitySelector>
            <ProductPrice>${(item.price * item.quantity).toFixed(2)}</ProductPrice>
          </ProductDetails>
          <RemoveIcon
            src="/imgs/close.png"
            alt="Remove item"
            onClick={() => removeItem(item.id)}
            loading="lazy"
          />
        </CartItem>
      ))}

      <SubTotal>
        <SubtotalSection />
        <SubtotalContainer>
          <SubtotalLabel>Subtotal</SubtotalLabel>
          <SubtotalAmount>${subtotal.toFixed(2)}</SubtotalAmount>
        </SubtotalContainer>
        <CheckoutDivider />
        <TaxShippingInfo>
          Tax included and shipping calculated at checkout
        </TaxShippingInfo>
        <CheckoutButton to="/payment">
          Proceed to checkout
        </CheckoutButton>
      </SubTotal>
    </>
  );
};

export default MyChart;
